document.addEventListener("DOMContentLoaded", () => {
  const e = document.getElementById("hash");
  $fx && $fx.hash && (e.innerHTML = "Hash: " + $fx.hash);
  document
    .getElementById("refresh-btn")
    .addEventListener("click", function (e) {
      location.reload();
    });
  let t = "";
  for (let [e, n] of Object.entries($fx._features))
    t += `<strong>${e}:</strong> ${n}<br>`;
  document.getElementById("metadata").innerHTML = t;
});
